import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long max_sum = 0;
        for (int i = 0; i < (1 << n); i++) {
            long current_sum = 0;
            boolean possible = true;
            long[] b = new long[n];
            for (int j = 0; j < n; j++) {
                b[j] = 0;
            }
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    boolean found = false;
                    for (int l = 0; l <= j; l++) {
                        if (j - l + 1 >= k) {
                            boolean all_one = true;
                            for (int m = l; m <= j; m++) {
                                if ((i >> m) % 2 == 0) {
                                    all_one = false;
                                    break;
                                }
                            }
                            if (all_one) {
                                for (int m = l; m <= j; m++) {
                                    b[m] = 1;
                                }
                                found = true;
                                break;
                            }
                        }
                    }
                    if (!found) {
                        possible = false;
                        break;
                    }
                }
            }
            if (possible) {
                for (int j = 0; j < n; j++) {
                    if (b[j] == 1) {
                        current_sum += a[j];
                    }
                }
                max_sum = Math.max(max_sum, current_sum);
            }
        }
        System.out.println(max_sum);
    }
}
