n, k = map(int, input().split())
a = list(map(int, input().split()))

dp = {}
def solve(i, prev_k):
    if i == n:
        return 0
    if (i, prev_k) in dp:
        return dp[(i, prev_k)]

    ans = solve(i + 1, prev_k)
    cur_sum = 0
    for j in range(i, min(i + k, n)):
        cur_sum += a[j]
        if prev_k == 0 or j - i +1 == k:
          ans = max(ans, cur_sum + solve(j + 1, j-i+1))

    dp[(i, prev_k)] = ans
    return ans

print(solve(0,0))
