#include <iostream>
#include <algorithm>

using namespace std;

int main() {
  long long aI, aO, aT, aJ, aL, aS, aZ;
  cin >> aI >> aO >> aT >> aJ >> aL >> aS >> aZ;

  long long total = aI + aO + aT + aJ + aL + aS + aZ;
  long long ans = 0;

  for (long long k = 1; k <= total; ++k) {
    long long num_tets = 2 * k;
    long long cur_aI = min(aI, num_tets);
    long long cur_aO = min(aO, num_tets / 2);
    long long cur_aT = min(aT, num_tets);
    long long cur_aJ = min(aJ, num_tets);
    long long cur_aL = min(aL, num_tets);
    long long cur_aS = min(aS, num_tets / 2);
    long long cur_aZ = min(aZ, num_tets / 2);

    if (cur_aI + 2 * cur_aO + cur_aT + cur_aJ + cur_aL + 2* cur_aS + 2 * cur_aZ >= num_tets) {
      ans = max(ans, k);
    }
  }

  cout << ans << endl;
  return 0;
}
