#include <stdio.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    int count = 0;
    int i;
    for (i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    int p[n];
    int used[n + 1];

    function(n, a, p, used, 0, &count);
    printf("%d\n", count);
    return 0;
}

void function(int n, int a[], int p[], int used[], int k, int *count) {
    if (k == n) {
        int flag = 1;
        for (int i = 0; i < n; i++) {
            if (p[i] != a[i] && p[p[i]-1] != a[i]) {
                flag = 0;
                break;
            }
        }
        if (flag) {
            (*count)++;
        }
        return;
    }
    for (int i = 1; i <= n; i++) {
        if (used[i] == 0) {
            used[i] = 1;
            p[k] = i;
            function(n, a, p, used, k + 1, count);
            used[i] = 0;
        }
    }
}
