#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
    a[i]--; 
  }

  long long count = 0;
  vector<int> p(n);
  vector<bool> used(n, false);
  function<void(int)> permute = [&](int k) {
    if (k == n) {
      bool ok = true;
      for (int i = 0; i < n; ++i) {
        if (p[i] != a[i] && p[p[i]] != a[i]) {
          ok = false;
          break;
        }
      }
      if (ok) count++;
      return;
    }

    for (int i = 0; i < n; ++i) {
      if (!used[i]) {
        p[k] = i;
        used[i] = true;
        permute(k + 1);
        used[i] = false;
      }
    }
  };

  permute(0);
  cout << count << endl;
  return 0;
}
