import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        long mod = 1000000007;
        long ans = 0;
        int[] p = new int[n];
        boolean[] used = new boolean[n + 1];
        long[] fact = new long[n + 1];
        fact[0] = 1;
        for (int i = 1; i <= n; i++) {
            fact[i] = (fact[i - 1] * i) % mod;
        }

        function(0, a, p, used, ans, fact, mod, n);
        System.out.println(ans);
    }

    static void function(int k, int[] a, int[] p, boolean[] used, long ans, long[] fact, long mod, int n){
        if(k == n){
            boolean ok = true;
            for(int i = 0; i < n; i++){
                if(p[i] != a[i] && p[p[i]-1] != a[i]){
                    ok = false;
                    break;
                }
            }
            if(ok) ans++;
            return;
        }

        for(int i = 1; i <= n; i++){
            if(!used[i]){
                p[k] = i;
                used[i] = true;
                function(k + 1, a, p, used, ans, fact, mod, n);
                used[i] = false;
            }
        }
    }
}
