def solve():
    n = int(input())
    a = list(map(int, input().split()))

    ans = -float('inf')
    for i in range(1 << n):
        smashed = [False] * n
        cur_ans = 0

        for j in range(n):
            if (i >> j) & 1:
                for k in range(j, n, j + 1):
                    smashed[k] = True

        for j in range(n):
            if not smashed[j]:
                cur_ans += a[j]
        ans = max(ans, cur_ans)
    print(ans)

solve()
