#include <stdio.h>
#include <string.h>

int main() {
    int n;
    scanf("%d", &n);
    char s[n + 1];
    scanf("%s", s);
    int count = 0;
    for (int i = 0; i < n - 2; i++) {
        if (s[i] == '1' && s[i + 1] == '0' && s[i + 2] == '1') {
            count++;
            s[i] = '0';
            s[i + 1] = '1';
            s[i + 2] = '0';
            i = -1;
        }
    }
    printf("%d\n", count);
    return 0;
}
