#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 200005

int n;
int a[MAXN], b[MAXN];
char s[MAXN];
int adj[MAXN][2];
int dist[MAXN];
int visited[MAXN];
int colors[MAXN];

void bfs(int start, int d) {
    memset(dist, -1, sizeof(dist));
    memset(visited, 0, sizeof(visited));
    int q[MAXN], head = 0, tail = 0;
    q[tail++] = start;
    dist[start] = 0;
    visited[start] = 1;
    while (head < tail) {
        int u = q[head++];
        if (dist[u] <= d) {
            for (int i = 0; i < adj[u][0]; ++i) {
                int v = adj[u][i + 1];
                if (!visited[v]) {
                    visited[v] = 1;
                    dist[v] = dist[u] + 1;
                    q[tail++] = v;
                }
            }
        }
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n - 1; ++i) {
        scanf("%d %d", &a[i], &b[i]);
        adj[a[i]][adj[a[i]][0]++] = b[i];
        adj[b[i]][adj[b[i]][0]++] = a[i];
    }
    scanf("%s", s);
    int count = 0;
    for (int i = 1; i <= n; ++i) {
        if (s[i - 1] == '1') {
            for (int d = 0; d < n; ++d) {
                memset(colors, 0, sizeof(colors));
                bfs(i, d);
                for (int j = 1; j <= n; ++j) {
                    if (visited[j]) colors[j] = 1;
                }
                int hash = 0;
                for (int j = 1; j <= n; ++j) {
                    hash = hash * 2 + colors[j];
                }
                count = count | (1 << hash);
            }
        }
    }
    printf("%d\n", __builtin_popcount(count));
    return 0;
}
