#include <iostream>
#include <vector>
#include <string>
#include <set>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<tuple<int, int>> edges(n - 1);
  for (int i = 0; i < n - 1; ++i) {
    cin >> get<0>(edges[i]) >> get<1>(edges[i]);
    get<0>(edges[i])--;
    get<1>(edges[i])--;
  }

  string s;
  cin >> s;

  set<vector<int>> distinct_colorings;

  for (int i = 0; i < n; ++i) {
    if (s[i] == '1') {
      for (int d = 0; d < n; ++d) {
        vector<int> coloring(n, 0);
        vector<int> q;
        q.push_back(i);
        vector<int> dist(n, -1);
        dist[i] = 0;
        int head = 0;

        while (head < q.size()) {
          int u = q[head++];
          if (dist[u] <= d) {
            coloring[u] = 1;
            for (const auto& edge : edges) {
              int v = -1;
              if (get<0>(edge) == u) v = get<1>(edge);
              if (get<1>(edge) == u) v = get<0>(edge);
              if (v != -1 && dist[v] == -1) {
                dist[v] = dist[u] + 1;
                q.push_back(v);
              }
            }
          }
        }
        distinct_colorings.insert(coloring);
      }
    }
  }

  cout << distinct_colorings.size() << endl;
  return 0;
}
