import itertools

def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))
    s = input()

    liked_vertices = [i for i, c in enumerate(s) if c == '1']

    adj = [[] for _ in range(n)]
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    def bfs(start_node, max_dist):
        q = [(start_node, 0)]
        visited = set()
        colored = set()
        while q:
            curr_node, dist = q.pop(0)
            if dist <= max_dist and curr_node not in visited:
                visited.add(curr_node)
                colored.add(curr_node)
                for neighbor in adj[curr_node]:
                    q.append((neighbor, dist + 1))
        return colored

    possible_colorings = set()
    for start_node in liked_vertices:
        for max_dist in range(n):
            colored_nodes = bfs(start_node, max_dist)
            coloring = ""
            for i in range(n):
                if i in colored_nodes:
                    coloring += "1"
                else:
                    coloring += "0"
            possible_colorings.add(coloring)

    print(len(possible_colorings))

