#include <stdio.h>

int main() {
    int N, M;
    scanf("%d", &N);
    int T[N];
    long long sum = 0;
    for (int i = 0; i < N; i++) {
        scanf("%d", &T[i]);
        sum += T[i];
    }
    scanf("%d", &M);
    for (int i = 0; i < M; i++) {
        int P, X;
        scanf("%d %d", &P, &X);
        long long result = sum - T[P - 1] + X;
        printf("%lld\n", result);
    }
    return 0;
}
