import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s = sc.next();
        int count = 0;
        char[] arr = s.toCharArray();
        for (int i = 0; i < n - 2; i++) {
            if (arr[i] == '1' && arr[i + 1] == '0' && arr[i + 2] == '1') {
                arr[i] = '0';
                arr[i + 1] = '1';
                arr[i + 2] = '0';
                count++;
                i = -1;
            }
        }
        System.out.println(count);
    }
}
