#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> t(n);
  for (int i = 0; i < n; ++i) {
    cin >> t[i];
  }

  int m;
  cin >> m;

  for (int i = 0; i < m; ++i) {
    int p;
    long long x;
    cin >> p >> x;
    long long total_time = 0;
    for (int j = 0; j < n; ++j) {
      if (j + 1 == p) {
        total_time += x;
      } else {
        total_time += t[j];
      }
    }
    cout << total_time << endl;
  }

  return 0;
}
