import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] t = new int[n];
        for (int i = 0; i < n; i++) {
            t[i] = sc.nextInt();
        }
        int m = sc.nextInt();
        for (int i = 0; i < m; i++) {
            int p = sc.nextInt() - 1;
            int x = sc.nextInt();
            long sum = 0;
            for (int j = 0; j < n; j++) {
                if (j == p) {
                    sum += x;
                } else {
                    sum += t[j];
                }
            }
            System.out.println(sum);
        }
    }
}
