#include <stdio.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    long long count = 0;
    int p[n];
    for (int i = 0; i < n; i++) {
        p[i] = i + 1;
    }
    do {
        int flag = 1;
        for (int i = 0; i < n; i++) {
            int left = 0;
            int right = 0;
            for (int j = 0; j < n; j++) {
                if (p[j] < p[i]) {
                    left++;
                } else if (p[j] > p[i]) {
                    right++;
                }
            }
            if (abs(left - right) != a[i]) {
                flag = 0;
                break;
            }
        }
        if (flag) {
            count++;
        }
    } while (next_permutation(p, p + n));
    printf("%lld\n", count);
    return 0;
}
