def solve():
    n = int(input())
    a = list(map(int, input().split()))

    mod = 10**9 + 7

    count = 0

    import itertools

    for perm in itertools.permutations(range(1, n + 1)):
        valid = True
        for i in range(n):
            left_count = 0
            right_count = 0
            for j in range(n):
                if perm[j] < perm[i]:
                    left_count += 1
                elif perm[j] > perm[i]:
                    right_count += 1
            if abs(left_count - right_count) != a[i]:
                valid = False
                break
        if valid:
            count += 1

    print(count % mod)

solve()
