import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long mod = 1000000007;
        long ans = 0;
        long limit = 1;
        while (limit <= n) {
            limit *= 2;
        }
        limit /= 2;
        long num = 0;
        long pow2 = 1;
        while (limit > 0) {
            long x = n / limit;
            long y = n % limit;
            long add = (x + 1) * x / 2 % mod * pow2 % mod * pow2 % mod;
            long sub = (pow2 - 1 + mod) % mod * pow2 % mod * x % mod * (y + 1) % mod;
            ans = (ans + add - sub + mod) % mod;
            pow2 = pow2 * 2 % mod;
            limit /= 2;
        }
        System.out.println(ans);
    }
}
