#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long count = 0;
  vector<int> p(n);
  vector<bool> used(n + 1, false);
  function<void(int)> permute = [&](int k) {
    if (k == n) {
      bool ok = true;
      for (int i = 0; i < n; ++i) {
        long long left = 0;
        long long right = 0;
        for (int j = 0; j < n; ++j) {
          if (j < i) {
            left++;
          } else if (j > i) {
            right++;
          }
        }
        if (abs(left - right) != a[i]) {
          ok = false;
          break;
        }
      }
      if (ok) {
        count++;
      }
      return;
    }

    for (int i = 1; i <= n; ++i) {
      if (!used[i]) {
        p[k] = i;
        used[i] = true;
        permute(k + 1);
        used[i] = false;
      }
    }
  };

  permute(0);
  cout << count << endl;
  return 0;
}
