import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        long count = 0;
        List<Integer> p = new ArrayList<>();
        for (int i = 1; i <= n; i++) {
            p.add(i);
        }
        long mod = 1000000007;
        do {
            boolean ok = true;
            for (int i = 0; i < n; i++) {
                int left = 0;
                int right = 0;
                for (int j = 0; j < n; j++) {
                    if (p.get(j) == i + 1) {
                        break;
                    }
                    left++;
                }
                for (int j = 0; j < n; j++) {
                    if (p.get(j) == i + 1) {
                        continue;
                    }
                    right++;
                }

                if (Math.abs(left - right) != a[i]) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                count++;
            }
        } while (Collections.nextPermutation(p));
        System.out.println(count);
    }
}
