#include <stdio.h>

long long power(long long base, long long exp, long long mod) {
    long long res = 1;
    base %= mod;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        exp /= 2;
    }
    return res;
}

int main() {
    long long n;
    scanf("%lld", &n);
    long long mod = 1e9 + 7;
    long long ans = power(2, 63, mod);
    long long k = 63;
    while ((1LL << k) > n + 1) k--;
    long long res = (power(2, 2 * k + 1, mod) + mod -1) % mod;
    printf("%lld\n", res);
    return 0;
}
