#include <iostream>

using namespace std;

int main() {
  long long n;
  cin >> n;

  long long mod = 1e9 + 7;
  long long ans = 0;
  for (long long u = 0; u <= n; ++u) {
    for (long long v = 0; v <= n; ++v) {
      bool found = false;
      for (long long a = 0; a <= v; ++a) {
        long long b = v - a;
        if (b < 0) continue;
        if ((a ^ b) == u) {
          found = true;
          break;
        }
      }
      if (found) ans = (ans + 1) % mod;
    }
  }
  cout << ans << endl;
  return 0;
}
