#include <iostream>
#include <vector>
#include <string>

using namespace std;

long long calculate(const vector<long long>& nums, const vector<char>& ops) {
    long long result = nums[0];
    for (size_t i = 0; i < ops.size(); ++i) {
        if (ops[i] == '+') {
            result += nums[i + 1];
        } else {
            result -= nums[i + 1];
        }
    }
    return result;
}

int main() {
    int n;
    cin >> n;

    vector<long long> nums(n);
    vector<char> ops(n - 1);

    for (int i = 0; i < n; ++i) {
        cin >> nums[i];
    }
    for (int i = 0; i < n - 1; ++i) {
        cin >> ops[i];
    }

    long long max_val = -1e18; 
    for (int i = 0; i < (1 << (n - 1)); ++i) {
        vector<long long> cur_nums;
        vector<char> cur_ops;
        long long cur_num = nums[0];
        char cur_op;

        for (int j = 0; j < n - 1; ++j) {
            if ((i >> j) & 1) {
                cur_nums.push_back(cur_num);
                cur_num = nums[j + 1];
                cur_ops.push_back(ops[j]);
            } else {
                cur_num = (ops[j] == '+') ? cur_num + nums[j + 1] : cur_num - nums[j+1];
            }
        }
        cur_nums.push_back(cur_num);
        max_val = max(max_val, calculate(cur_nums, cur_ops));
    }
    cout << max_val << endl;
    return 0;
}
