def solve():
    line = input().split()
    n = int(line[0])
    a = []
    ops = []
    for i in range(1, len(line), 2):
        a.append(int(line[i]))
        if i + 1 < len(line):
            ops.append(line[i+1])

    def calculate(arr, op):
        res = arr[0]
        for i in range(len(op)):
            if op[i] == '+':
                res += arr[i+1]
            else:
                res -= arr[i+1]
        return res

    def find_max(arr, ops):
        if len(arr) == 1:
            return arr[0]

        max_val = -float('inf')
        for i in range(1, len(arr)):
            left_arr = arr[:i]
            right_arr = arr[i:]
            left_ops = ops[:i-1]
            right_ops = ops[i-1:]

            left_val = find_max(left_arr, left_ops) if len(left_arr) > 1 else left_arr[0]
            right_val = find_max(right_arr, right_ops) if len(right_arr) > 1 else right_arr[0]

            if ops[i-1] == '+':
                max_val = max(max_val, left_val + right_val)
            else:
                max_val = max(max_val, left_val - right_val)

        return max_val

    print(find_max(a, ops))

