#include <stdio.h>
#include <stdlib.h>

long long calculate_score(long long *t, int n, int *solved) {
    long long score = 0;
    long long time = 0;
    for (int i = 0; i < n; i++) {
        if (solved[i])
            time += t[i];
    }
    long long count = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i; j < n; j++) {
            int all_solved = 1;
            for (int k = i; k <= j; k++) {
                if (!solved[k]) {
                    all_solved = 0;
                    break;
                }
            }
            if (all_solved)
                count++;
        }
    }
    return count - time;
}

int main() {
    int n;
    scanf("%d", &n);
    long long *t = (long long *)malloc(n * sizeof(long long));
    for (int i = 0; i < n; i++) {
        scanf("%lld", &t[i]);
    }
    int m;
    scanf("%d", &m);
    for (int i = 0; i < m; i++) {
        int p;
        long long x;
        scanf("%d %lld", &p, &x);
        long long *temp_t = (long long *)malloc(n * sizeof(long long));
        for (int j = 0; j < n; j++) {
            temp_t[j] = t[j];
        }
        temp_t[p - 1] = x;
        long long max_score = 0;
        for (int j = 0; j < (1 << n); j++) {
            int *solved = (int *)malloc(n * sizeof(int));
            for (int k = 0; k < n; k++) {
                if ((j >> k) & 1)
                    solved[k] = 1;
                else
                    solved[k] = 0;
            }
            max_score = (max_score > calculate_score(temp_t, n, solved)) ? max_score : calculate_score(temp_t, n, solved);
            free(solved);
        }
        printf("%lld\n", max_score);
        free(temp_t);
    }
    free(t);
    return 0;
}
