#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n;
  cin >> n;

  vector<long long> t(n);
  for (int i = 0; i < n; ++i) {
    cin >> t[i];
  }

  long long m;
  cin >> m;

  for (int i = 0; i < m; ++i) {
    long long p, x;
    cin >> p >> x;
    p--; 

    vector<long long> cur_t = t;
    cur_t[p] = x;

    long long max_score = 0;
    for (int j = 0; j < (1 << n); ++j) {
      long long cur_time = 0;
      vector<bool> solved(n, false);
      for (int k = 0; k < n; ++k) {
        if ((j >> k) & 1) {
          cur_time += cur_t[k];
          solved[k] = true;
        }
      }

      long long cur_score = 0;
      for (long long l = 0; l < n; ++l) {
        for (long long r = l; r < n; ++r) {
          bool all_solved = true;
          for (long long k = l; k <= r; ++k) {
            if (!solved[k]) {
              all_solved = false;
              break;
            }
          }
          if (all_solved) {
            cur_score++;
          }
        }
      }
      max_score = max(max_score, cur_score - cur_time);
    }
    cout << max_score << endl;
  }
  return 0;
}
