import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] t = new long[n];
        for (int i = 0; i < n; i++) {
            t[i] = sc.nextLong();
        }
        int m = sc.nextInt();
        long[] p = new long[m];
        long[] x = new long[m];
        for (int i = 0; i < m; i++) {
            p[i] = sc.nextInt() -1;
            x[i] = sc.nextLong();
        }

        for (int i = 0; i < m; i++) {
            long[] cur_t = Arrays.copyOf(t, n);
            cur_t[(int)p[i]] = x[i];
            long max_score = 0;
            for (int j = 0; j < (1 << n); j++) {
                long cur_time = 0;
                List<Integer> solved = new ArrayList<>();
                for (int k = 0; k < n; k++) {
                    if ((j >> k) % 2 == 1) {
                        cur_time += cur_t[k];
                        solved.add(k);
                    }
                }
                long cur_score = 0;
                for (int k = 0; k < n; k++) {
                    for (int l = k; l < n; l++) {
                        boolean ok = true;
                        for (int z = k; z <= l; z++) {
                            boolean found = false;
                            for (int s : solved) {
                                if (s == z) {
                                    found = true;
                                    break;
                                }
                            }
                            if (!found) {
                                ok = false;
                                break;
                            }
                        }
                        if (ok) cur_score++;
                    }
                }
                max_score = Math.max(max_score, cur_score - cur_time);
            }
            System.out.println(max_score);
        }
    }
}
