import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int a = sc.nextInt();
        int b = sc.nextInt();
        long mod = 1000000007;
        long[][] dp = new long[a + 1][b + 1];
        dp[0][0] = 1;
        for (int i = 0; i <= a; i++) {
            for (int j = 0; j <= b; j++) {
                if (i > 0) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][j]) % mod;
                }
                if (j > 0) {
                    dp[i][j] = (dp[i][j] + dp[i][j - 1]) % mod;
                }
                if (i > 0 && j > 0) {
                    dp[i][j] = (dp[i][j] - dp[i - 1][j - 1] + mod) % mod;
                }
            }
        }
        long ans = dp[a][b];
        System.out.println(ans);
    }
}
