#include <stdio.h>

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    char C[H][W];
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            scanf(" %c", &C[i][j]);
        }
    }
    for (int i = 0; i < 2 * H; i++) {
        for (int j = 0; j < W; j++) {
            printf("%c", C[i / 2][j]);
        }
        printf("\n");
    }
    return 0;
}
