#include <stdio.h>
#include <string.h>

int main() {
    char s[100001];
    scanf("%s", s);
    int n = strlen(s);
    int dp[n + 1];
    memset(dp, 0, sizeof(dp));
    dp[0] = 1;
    for (int i = 0; i < n; i++) {
        if (dp[i] == 0) continue;
        if (i + 5 <= n && strncmp(s + i, "dream", 5) == 0) dp[i + 5] = 1;
        if (i + 7 <= n && strncmp(s + i, "dreamer", 7) == 0) dp[i + 7] = 1;
        if (i + 5 <= n && strncmp(s + i, "erase", 5) == 0) dp[i + 5] = 1;
        if (i + 6 <= n && strncmp(s + i, "eraser", 6) == 0) dp[i + 6] = 1;
    }
    if (dp[n]) printf("YES\n");
    else printf("NO\n");
    return 0;
}
