#include <stdio.h>
#include <string.h>
#include <stdlib.h>

char *f(char *s) {
    int n = strlen(s);
    char *min_s = strdup(s);
    for (int i = 1; i < n; i++) {
        char *shifted = (char *)malloc(n + 1);
        strncpy(shifted, s + i, n - i);
        strncpy(shifted + n - i, s, i);
        shifted[n] = '\0';
        if (strcmp(shifted, min_s) < 0) {
            free(min_s);
            min_s = shifted;
        } else {
            free(shifted);
        }
    }
    return min_s;
}

int main() {
    int x, y, z;
    scanf("%d %d %d", &x, &y, &z);
    char *best_f = NULL;
    int len = x + y + z;
    char *s = (char *)malloc(len + 1);

    for (int i = 0; i < (1 << (x + y + z)); ++i) {
        int cur_x = 0, cur_y = 0, cur_z = 0;
        int k=i;
        for (int j = 0; j < len; ++j) {
            if (cur_x < x && k % 3 == 0) {
                s[j] = 'a';
                cur_x++;
            } else if (cur_y < y && k % 3 == 1) {
                s[j] = 'b';
                cur_y++;
            } else {
                s[j] = 'c';
                cur_z++;
            }
            k /= 3;
        }
        s[len] = '\0';
        if (cur_x == x && cur_y == y && cur_z == z) {
            char *cur_f = f(s);
            if (best_f == NULL || strcmp(cur_f, best_f) > 0) {
                free(best_f);
                best_f = cur_f;
            } else {
                free(cur_f);
            }
        }
    }
    printf("%s\n", best_f);
    free(s);
    free(best_f);
    return 0;
}
