import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        String[] words = {"dream", "dreamer", "erase", "eraser"};
        int n = s.length();
        boolean[][] dp = new boolean[n + 1][4];
        dp[0][0] = dp[0][1] = dp[0][2] = dp[0][3] = true;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 4; j++) {
                if (dp[i][j]) {
                    if (i + words[j].length() <= n && s.substring(i, i + words[j].length()).equals(words[j])) {
                        dp[i + words[j].length()][j] = true;
                    }
                    for (int k = 0; k < 4; k++) {
                        if (i + words[k].length() <= n && s.substring(i, i + words[k].length()).equals(words[k])) {
                            dp[i + words[k].length()][k] = true;
                        }
                    }
                }
            }
        }
        if (dp[n][0] || dp[n][1] || dp[n][2] || dp[n][3]) {
            System.out.println("YES");
        } else {
            System.out.println("NO");
        }
    }
}
