#include <stdio.h>
#include <stdlib.h>

int get_root(int *parent, int i) {
    if (parent[i] == i)
        return i;
    return parent[i] = get_root(parent, parent[i]);
}

void unite(int *parent, int a, int b) {
    a = get_root(parent, a);
    b = get_root(parent, b);
    if (a != b)
        parent[a] = b;
}

int main() {
    int n, k, l;
    scanf("%d %d %d", &n, &k, &l);
    int *road_parent = (int *)malloc((n + 1) * sizeof(int));
    int *rail_parent = (int *)malloc((n + 1) * sizeof(int));
    for (int i = 1; i <= n; i++) {
        road_parent[i] = i;
        rail_parent[i] = i;
    }
    for (int i = 0; i < k; i++) {
        int p, q;
        scanf("%d %d", &p, &q);
        unite(road_parent, p, q);
    }
    for (int i = 0; i < l; i++) {
        int r, s;
        scanf("%d %d", &r, &s);
        unite(rail_parent, r, s);
    }
    for (int i = 1; i <= n; i++) {
        int count = 0;
        for (int j = 1; j <= n; j++) {
            if (get_root(road_parent, i) == get_root(road_parent, j) &&
                get_root(rail_parent, i) == get_root(rail_parent, j))
                count++;
        }
        printf("%d ", count);
    }
    printf("\n");
    free(road_parent);
    free(rail_parent);
    return 0;
}

