#include <iostream>
#include <vector>

using namespace std;

int get_root(vector<int>& parent, int i) {
  if (parent[i] == i) return i;
  return parent[i] = get_root(parent, parent[i]);
}

void unite_sets(vector<int>& parent, vector<int>& rank, int a, int b) {
  a = get_root(parent, a);
  b = get_root(parent, b);
  if (a != b) {
    if (rank[a] < rank[b]) swap(a, b);
    parent[b] = a;
    if (rank[a] == rank[b]) rank[a]++;
  }
}

int main() {
  int n, k, l;
  cin >> n >> k >> l;

  vector<int> parent_roads(n), rank_roads(n, 0);
  vector<int> parent_rails(n), rank_rails(n, 0);
  for (int i = 0; i < n; ++i) {
    parent_roads[i] = i;
    parent_rails[i] = i;
  }

  for (int i = 0; i < k; ++i) {
    int u, v;
    cin >> u >> v;
    unite_sets(parent_roads, rank_roads, u - 1, v - 1);
  }
  for (int i = 0; i < l; ++i) {
    int u, v;
    cin >> u >> v;
    unite_sets(parent_rails, rank_rails, u - 1, v - 1);
  }

  for (int i = 0; i < n; ++i) {
    int count = 0;
    for (int j = 0; j < n; ++j) {
      if (get_root(parent_roads, i) == get_root(parent_roads, j) &&
          get_root(parent_rails, i) == get_root(parent_rails, j)) {
        count++;
      }
    }
    cout << count << (i == n - 1 ? "" : " ");
  }
  cout << endl;

  return 0;
}
