import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        int l = sc.nextInt();
        List<int[]> roads = new ArrayList<>();
        for (int i = 0; i < k; i++) {
            roads.add(new int[]{sc.nextInt(), sc.nextInt()});
        }
        List<int[]> railways = new ArrayList<>();
        for (int i = 0; i < l; i++) {
            railways.add(new int[]{sc.nextInt(), sc.nextInt()});
        }
        for (int i = 1; i <= n; i++) {
            Set<Integer> roadConnected = new HashSet<>();
            Set<Integer> railConnected = new HashSet<>();
            Queue<Integer> q = new LinkedList<>();
            q.add(i);
            roadConnected.add(i);
            while (!q.isEmpty()) {
                int curr = q.poll();
                for (int[] road : roads) {
                    if (road[0] == curr && !roadConnected.contains(road[1])) {
                        q.add(road[1]);
                        roadConnected.add(road[1]);
                    } else if (road[1] == curr && !roadConnected.contains(road[0])) {
                        q.add(road[0]);
                        roadConnected.add(road[0]);
                    }
                }
            }
            q = new LinkedList<>();
            q.add(i);
            railConnected.add(i);
            while (!q.isEmpty()) {
                int curr = q.poll();
                for (int[] rail : railways) {
                    if (rail[0] == curr && !railConnected.contains(rail[1])) {
                        q.add(rail[1]);
                        railConnected.add(rail[1]);
                    } else if (rail[1] == curr && !railConnected.contains(rail[0])) {
                        q.add(rail[0]);
                        railConnected.add(rail[0]);
                    }
                }
            }
            int count = 0;
            for (int j = 1; j <= n; j++) {
                if (roadConnected.contains(j) && railConnected.contains(j)) {
                    count++;
                }
            }
            System.out.print(count + " ");
        }
        System.out.println();
        sc.close();
    }
}
