#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

typedef struct {
    long long x, y;
} Point;

long long dist(Point p1, Point p2) {
    return abs(p1.x - p2.x) + abs(p1.y - p2.y);
}

int main() {
    int n, a, b;
    scanf("%d %d %d", &n, &a, &b);
    Point points[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &points[i].x, &points[i].y);
    }
    a--;
    b--;
    long long initial_dist = dist(points[a], points[b]);
    int visited[MAXN] = {0};
    int q[MAXN], head = 0, tail = 0;
    q[tail++] = a;
    q[tail++] = b;
    visited[a] = visited[b] = 1;
    int count = 0;
    while (head < tail) {
        int u = q[head++];
        count++;
        for (int v = 0; v < n; v++) {
            if (u == v) continue;
            if (dist(points[u], points[v]) == initial_dist && !visited[v]) {
                visited[v] = 1;
                q[tail++] = v;
            }
        }
    }
    printf("%d\n", count);
    return 0;
}
