#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

int main() {
    int n, a, b;
    cin >> n >> a >> b;

    vector<pair<long long, long long>> points(n);
    for (int i = 0; i < n; ++i) {
        cin >> points[i].first >> points[i].second;
    }

    auto manhattan_distance = [&](int i, int j) {
        return abs(points[i].first - points[j].first) + abs(points[i].second - points[j].second);
    };

    vector<bool> visited(n, false);
    vector<pair<int, int>> q;
    q.push_back({a - 1, b - 1});
    visited[a - 1] = true;
    visited[b - 1] = true;
    int head = 0;
    int count = 0;

    while (head < q.size()) {
        pair<int, int> current = q[head++];
        count++;
        long long dist = manhattan_distance(current.first, current.second);
        for (int i = 0; i < n; ++i) {
            if (!visited[i]) {
                if (manhattan_distance(current.first, i) == dist) {
                    q.push_back({current.first, i});
                    visited[i] = true;
                } else if (manhattan_distance(current.second, i) == dist) {
                    q.push_back({current.second, i});
                    visited[i] = true;
                }
            }
        }
    }
    cout << count << endl;
    return 0;
}
