#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

string solve() {
  int x, y, z;
  cin >> x >> y >> z;

  string s = "";
  for (int i = 0; i < x; ++i) s += 'a';
  for (int i = 0; i < y; ++i) s += 'b';
  for (int i = 0; i < z; ++i) s += 'c';

  string best_f = s;
  sort(s.begin(), s.end());
  do {
    string t = s;
    string f = t;
    for (int i = 0; i < t.length(); ++i) {
      string shifted = t.substr(i) + t.substr(0, i);
      if (shifted < f) {
        f = shifted;
      }
    }
    if (f > best_f) best_f = f;
  } while (next_permutation(s.begin(), s.end()));

  return best_f;
}

int main() {
  cout << solve() << endl;
  return 0;
}
