import itertools

def manhattan_distance(p1, p2):
    return abs(p1[0] - p2[0]) + abs(p1[1] - p2[1])

def solve():
    n, a, b = map(int, input().split())
    points = []
    for _ in range(n):
        points.append(list(map(int, input().split())))

    a -= 1
    b -= 1

    initial_distance = manhattan_distance(points[a], points[b])

    reachable = { (min(a,b), max(a,b)) }
    q = [ (a,b) ]

    while q:
        p1, p2 = q.pop(0)
        for p3 in range(n):
            if (min(p1,p3), max(p1,p3)) not in reachable and manhattan_distance(points[p1],points[p2]) == manhattan_distance(points[p1], points[p3]):
                reachable.add((min(p1,p3), max(p1,p3)))
                q.append((p1,p3))

    print(len(reachable))

