#include <stdio.h>
#include <string.h>

#define MOD 1000000007

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    char s[n + 1];
    scanf("%s", s);
    int l[m], r[m];
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &l[i], &r[i]);
        l[i]--; 
        r[i]--; 
    }
    long long count = 0;
    int ones[n];
    for (int i = 0; i < n; i++) {
        ones[i] = s[i] - '0';
    }
    int total_ones = 0;
    for (int i = 0; i < n; i++) {
        total_ones += ones[i];
    }
    long long dp[n + 1][n + 1];
    memset(dp, 0, sizeof(dp));
    dp[0][0] = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= i; j++) {
            if (dp[i][j] == 0) continue;
            dp[i + 1][j + 1] = (dp[i + 1][j + 1] + dp[i][j]) % MOD;
            dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % MOD;
        }
    }

    long long ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + dp[n][i]) % MOD;
    }

    printf("%lld\n", ans);
    return 0;
}
