import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        String s = sc.next();
        int[] l = new int[m];
        int[] r = new int[m];
        for (int i = 0; i < m; i++) {
            l[i] = sc.nextInt();
            r[i] = sc.nextInt();
        }
        sc.close();

        Set<String> set = new HashSet<>();
        dfs(s, l, r, 0, set);
        System.out.println(set.size());
    }

    static void dfs(String s, int[] l, int[] r, int k, Set<String> set) {
        if (k == l.length) {
            set.add(s);
            return;
        }
        int len = r[k] - l[k] + 1;
        int[] cnt = new int[2];
        for (int i = l[k] - 1; i < r[k]; i++) {
            cnt[s.charAt(i) - '0']++;
        }
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < (1 << len); i++) {
            if (Integer.bitCount(i) != cnt[1]) continue;
            sb.setLength(0);
            sb.append(s.substring(0, l[k] - 1));
            int cur = i;
            for (int j = 0; j < len; j++) {
                sb.append((cur % 2));
                cur /= 2;
            }
            sb.append(s.substring(r[k]));
            dfs(sb.toString(), l, r, k + 1, set);
        }
    }
}
