MOD = 1000000007

def solve():
    n, m = map(int, input().split())
    s = list(input())
    ranges = []
    for _ in range(m):
        ranges.append(list(map(int, input().split())))

    groups = []
    current_group = []
    for i in range(n):
        current_group.append(i)
        for r in ranges:
            if r[0] -1 <= i < r[1]:
                break
        else:
            groups.append(current_group)
            current_group = []
    if current_group:
        groups.append(current_group)


    ans = 1
    for group in groups:
        zeros = 0
        ones = 0
        for i in group:
            if s[i] == '0':
                zeros += 1
            else:
                ones += 1
        ans = (ans * (zeros + ones + 1)) % MOD

    print(ans)


solve()
