import java.util.*;

public class Main {
    public static String solve() {
        Scanner sc = new Scanner(System.in);
        int x = sc.nextInt();
        int y = sc.nextInt();
        int z = sc.nextInt();
        String ans = "";
        for (int i = 0; i < (1 << (x + y + z)); ++i) {
            StringBuilder sb = new StringBuilder();
            int cur = i;
            int cnt_a = 0;
            int cnt_b = 0;
            int cnt_c = 0;
            for (int j = 0; j < x + y + z; ++j) {
                if (cnt_a < x && (cur % 3 == 0)) {
                    sb.append('a');
                    cnt_a++;
                } else if (cnt_b < y && (cur % 3 == 1)) {
                    sb.append('b');
                    cnt_b++;
                } else if (cnt_c < z && (cur % 3 == 2)) {
                    sb.append('c');
                    cnt_c++;
                } else {
                    sb.append(' ');
                }
                cur /= 3;
            }
            if (cnt_a == x && cnt_b == y && cnt_c == z) {
                String s = sb.toString().replaceAll(" ", "");
                String min_s = s;
                for (int j = 0; j < s.length(); ++j) {
                    String shifted = s.substring(j) + s.substring(0, j);
                    min_s = min_s.compareTo(shifted) < 0 ? min_s : shifted;
                }

                if (ans.compareTo(min_s) < 0) {
                    ans = min_s;
                }
            }

        }
        return ans;

    }

    public static void main(String[] args) {
        System.out.println(solve());
    }
}
