#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, x;
  cin >> n >> x;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long ans = 0;
  for (int i = 0; i < n - 1; ++i) {
    if (a[i] + a[i + 1] > x) {
      long long diff = a[i] + a[i + 1] - x;
      ans += diff;
      a[i + 1] -= diff;
    }
  }

  cout << ans << endl;
  return 0;
}
