#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
  string s;
  cin >> s;

  if (s.length() < 3) {
    cout << "Second" << endl;
    return 0;
  }

  bool can_move = false;
  for (int i = 1; i < s.length() - 1; ++i) {
    string next_s = s.substr(0, i) + s.substr(i + 1);
    bool ok = true;
    for (int j = 0; j < next_s.length() - 1; ++j) {
      if (next_s[j] == next_s[j + 1]) {
        ok = false;
        break;
      }
    }
    if (ok) {
      can_move = true;
      break;
    }
  }

  if (can_move) {
    cout << "First" << endl;
  } else {
    cout << "Second" << endl;
  }
  return 0;
}
