#include <stdio.h>

int main() {
    long long n, x;
    scanf("%lld %lld", &n, &x);
    long long a[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long ans = 0;
    for (long long i = 0; i < n - 1; i++) {
        long long sum = a[i] + a[i + 1];
        if (sum > x) {
            long long diff = sum - x;
            ans += diff;
            a[i + 1] -= diff;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
