import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long x = sc.nextLong();
        long[] a = new long[(int) n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long ans = 0;
        for (int i = 0; i < n - 1; i++) {
            long sum = a[i] + a[i + 1];
            if (sum > x) {
                long diff = sum - x;
                ans += diff;
                a[i + 1] -= diff;
            }
        }
        System.out.println(ans);
    }
}
