#include <iostream>
#include <string>

using namespace std;

int main() {
  string s;
  cin >> s;

  if (s.length() < 3) {
    cout << "Second" << endl;
    return 0;
  }

  bool can_remove = false;
  for (int i = 1; i < s.length() - 1; ++i) {
    string temp = s;
    temp.erase(i, 1);
    bool ok = true;
    for (int j = 0; j < temp.length() - 1; ++j) {
      if (temp[j] == temp[j + 1]) {
        ok = false;
        break;
      }
    }
    if (ok) {
      can_remove = true;
      break;
    }
  }

  if (can_remove) {
    cout << "First" << endl;
  } else {
    cout << "Second" << endl;
  }
  return 0;
}
