#include <stdio.h>
#include <stdlib.h>
#include <math.h>

struct Point {
    double x, y;
};

struct Circle {
    struct Point center;
    double radius;
};

double dist(struct Point p1, struct Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

int main() {
    struct Point start, end;
    int n;
    scanf("%lf %lf %lf %lf", &start.x, &start.y, &end.x, &end.y);
    scanf("%d", &n);
    struct Circle circles[n];
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf %lf", &circles[i].center.x, &circles[i].center.y, &circles[i].radius);
    }
    double min_exposed_time = dist(start, end);
    printf("%.12lf\n", min_exposed_time);
    return 0;
}
