import java.util.*;
import java.lang.Math;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        double xs = sc.nextDouble();
        double ys = sc.nextDouble();
        double xt = sc.nextDouble();
        double yt = sc.nextDouble();
        int n = sc.nextInt();
        double[] x = new double[n];
        double[] y = new double[n];
        double[] r = new double[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextDouble();
            y[i] = sc.nextDouble();
            r[i] = sc.nextDouble();
        }
        double dist = Math.sqrt(Math.pow(xs - xt, 2) + Math.pow(ys - yt, 2));
        double ans = dist;
        for (int i = 0; i < (1 << n); i++) {
            double cur_ans = 0;
            double cur_x = xs;
            double cur_y = ys;
            List<Integer> used = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    used.add(j);
                }
            }
            for (int j : used) {
                double dist_to_center = Math.sqrt(Math.pow(cur_x - x[j], 2) + Math.pow(cur_y - y[j], 2));
                if (dist_to_center > r[j]) {
                    continue;
                }
                double dist_to_point = Math.sqrt(Math.pow(xt - x[j], 2) + Math.pow(yt - y[j], 2));
                double angle = Math.atan2(yt - y[j], xt - x[j]);
                double new_x = x[j] + r[j] * Math.cos(angle);
                double new_y = y[j] + r[j] * Math.sin(angle);
                cur_ans += Math.sqrt(Math.pow(cur_x - new_x, 2) + Math.pow(cur_y - new_y, 2));
                cur_x = new_x;
                cur_y = new_y;
            }
            cur_ans += Math.sqrt(Math.pow(cur_x - xt, 2) + Math.pow(cur_y - yt, 2));
            ans = Math.min(ans, cur_ans);
        }
        System.out.printf("%.10f\n", ans);
    }
}
